import { useState } from 'react';
import useFetch from '../utils/useFetch';
import Info from './Info';

export default function CountrySelect() {
  const [selectedCountry, setCountry] = useState('GBR');
  const countries = useFetch('https://covid19.mathdro.id/api/countries');

  if (!countries) return null;

  const countryArr = Object.entries(countries.countries).map(([key, value]) => {
    return {
      name: `${key}`,
      code: `${value}`
    };
  });

  return (
    <div>
      <h2>Showing: {selectedCountry}</h2>
      <select onChange={(event) => setCountry(event.target.value)}>
        {countryArr.map((country) => (
          <option
            value={country.code}
            key={country.name}
            selected={selectedCountry === country.code}>
            {country.name}
          </option>
        ))}
      </select>
      <Info
        url={`https://covid19.mathdro.id/api/countries/${selectedCountry}`}></Info>
    </div>
  );
}

